function Hero() {
  return (
    <section
      className="h-[70vh] bg-cover bg-center flex items-center"
      style={{
        backgroundImage:
          "url('https://images.pexels.com/photos/1370295/pexels-photo-1370295.jpeg')",
      }}
    >
      <div className="bg-black/50 w-full h-full flex items-center">
        <div className="max-w-4xl mx-auto px-6 text-white">
          <h1 className="text-6xl font-extrabold">
            Find Your Next Favorite Book
          </h1>

          <p className="text-xl mt-4">
            Discover thousands of books across all categories.
          </p>
        </div>
      </div>
    </section>
  );
}

export default Hero;
