function Hero() {
  return (
    <section className="bg-[#F5F0E9] py-20">
      <div className="max-w-6xl mx-auto flex items-center gap-12 px-6">
        {/* IMAGE */}
        <img
          src="https://m.media-amazon.com/images/I/81bGKUa1e0L.jpg"
          className="w-60 h-80 object-cover shadow-xl rounded-lg"
        />

        {/* TEXT */}
        <div>
          <h1 className="text-5xl font-bold text-[#2E563F]">
            Your Journey Starts with a Book
          </h1>

          <p className="text-[#4A5C4F] text-lg mt-4">
            Explore a world of knowledge, stories, and inspiration.
          </p>

          <button className="mt-6 bg-[#2E563F] px-6 py-3 rounded-xl text-white font-semibold hover:bg-[#244C36]">
            Explore Now
          </button>
        </div>
      </div>
    </section>
  );
}

export default Hero;
