function Hero() {
  return (
    <section className="bg-[#F5F0E9] py-24">
      <div className="max-w-6xl mx-auto text-center px-6">
        {/* Title */}
        <h1 className="text-5xl font-extrabold text-[#2E563F] leading-tight">
          Discover Your Next <br /> Favorite Book
        </h1>

        {/* Subtitle */}
        <p className="text-lg text-[#4A5C4F] mt-4">
          Explore a curated collection of books designed to inspire, educate,
          and entertain.
        </p>

        {/* Button */}
        <button className="mt-8 bg-[#2E563F] hover:bg-[#244C36] text-white font-semibold px-8 py-3 rounded-lg shadow-md transition">
          Browse Collection
        </button>

        {/* Optional image */}
        <div className="mt-12 flex justify-center">
          <img
            src="https://i.imgur.com/WZx3K6n.png"
            alt="Books illustration"
            className="w-[420px] opacity-90"
          />
        </div>
      </div>
    </section>
  );
}

export default Hero;
