function Hero() {
  return (
    // <section className="bg-[#F5F0E9] py-32">
    //   <div className="max-w-6xl mx-auto text-center px-6 mt-10">
    //     <h1 className="text-6xl font-extrabold text-[#2E563F] leading-tight">
    //       Your Online Bookstore
    //     </h1>
    //     <p className="text-lg text-[#4A5C4F] mt-6 max-w-2xl mx-auto">
    //       Browse through our hand-picked collection of best-selling and
    //       inspiring books.
    //     </p>
    //     <button className="mt-10 bg-[#2E563F] hover:bg-[#244C36] text-white font-semibold px-8 py-3 rounded-xl shadow-lg transition transform hover:scale-105">
    //       Start Reading
    //     </button>
    //   </div>
    // </section>
    <section
      className="w-full h-[70vh] bg-cover bg-center flex items-center justify-center"
      style={{
        backgroundImage:
          "url('https://images.unsplash.com/photo-1513475382585-d06e58bcb0ea?auto=format&fit=crop&w=1400&q=80')",
      }}
    >
      <div className="bg-black/40 p-10 rounded-xl text-center max-w-3xl">
        <h1 className="text-5xl font-extrabold text-white leading-tight drop-shadow-lg">
          Discover Your Next <br /> Favorite Book
        </h1>

        <p className="text-lg text-gray-200 mt-4">
          Explore a curated collection of books designed to inspire, educate,
          and entertain.
        </p>

        <button className="mt-8 bg-[#2E563F] hover:bg-[#244C36] text-white font-semibold px-8 py-3 rounded-xl shadow-md transition">
          Browse Collection
        </button>
      </div>
    </section>
  );
}

export default Hero;
