import { useParams, useNavigate } from "react-router-dom";
import books from "../data/Books";
import { useState } from "react";

function BuyForm() {
  const { id } = useParams();
  const navigate = useNavigate();

  const book = books.find((b) => b.id === parseInt(id));

  // إذا الكتاب مش موجود
  if (!book) {
    return (
      <h1 className="text-center text-3xl font-bold mt-20 text-[#2E563F]">
        Book not found
      </h1>
    );
  }

  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [email, setEmail] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    // رسالة نجاح
    alert(`Order placed successfully for "${book.title}"!`);

    // نرجع المستخدم عالـ Home بعد 2 ثواني
    setTimeout(() => {
      navigate("/");
    }, 1500);
  };

  return (
    <div className="max-w-xl mx-auto px-6 py-20 bg-[#F5F0E9] rounded-xl shadow-lg mt-10">
      <h1 className="text-4xl font-extrabold mb-8 text-[#2E563F] text-center">
        Buy: {book.title}
      </h1>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Name */}
        <div>
          <label className="font-semibold text-[#2E563F]">Your Name</label>
          <input
            type="text"
            className="w-full border border-[#D6CEC2] p-3 rounded-lg bg-white"
            placeholder="Enter your full name"
            required
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>

        {/* Email */}
        <div>
          <label className="font-semibold text-[#2E563F]">Email</label>
          <input
            type="email"
            className="w-full border border-[#D6CEC2] p-3 rounded-lg bg-white"
            placeholder="example@email.com"
            required
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>

        {/* Address */}
        <div>
          <label className="font-semibold text-[#2E563F]">
            Shipping Address
          </label>
          <textarea
            className="w-full border border-[#D6CEC2] p-3 rounded-lg bg-white"
            placeholder="Enter your full address"
            required
            value={address}
            onChange={(e) => setAddress(e.target.value)}
          ></textarea>
        </div>

        {/* Submit Button */}
        <button
          type="submit"
          className="w-full bg-[#2E563F] hover:bg-[#244C36] text-white py-3 rounded-lg text-lg font-semibold transition"
        >
          Confirm Order
        </button>
      </form>
    </div>
  );
}

export default BuyForm;
